SELECT
(SELECT a.filerealname FROM useraudiomark a WHERE a.`filerealname`= e.`realname` AND a.`username`='admin' AND a.`mark`='1')<=>NULL mark
,e.*
FROM audiofilemain e
WHERE e.realname IN (SELECT a.filerealname FROM useraudiohistory a
WHERE a.`username` = #{userName }

AND a.`filerealname`= #{fileRealName }

and 1=1 )
ORDER BY e.realname
LIMIT #{start},#{size}

SELECT
(SELECT a.filerealname FROM useraudiomark a WHERE a.`filerealname`= e.`realname` AND a.`username`='admin' AND a.`mark`='1')<=>NULL mark
,e.*
FROM audiofilemain e
WHERE e.realname IN (SELECT a.filerealname FROM useraudiomark a
WHERE a.`username` = #{userName }

    AND a.`filerealname`= #{fileRealName }

and a.mark = 1 )
ORDER BY e.realname
LIMIT #{start},#{size}

INSERT INTO ccaudio . audiofilemain
(realname, name, path, length, size, author, uploadtime, effect, remark)
/* 大小写要对应 */
VALUES (#{realName },#{name},#{path},#{length},#{size},#{author},#{uploadTime},#{effect},#{remark});
